import React, { Component } from 'react';

const GameMap = (props) => {
  const flattenMap = [].concat(...props.map);
  const renderMap = flattenMap.map(cell => {
    let cellContent = cell.winnie ? "Winnie": cell.win? "Winnie found Wonton" : cell.wonton ? "Wonton" : cell.tracked ? "Paw" : null;

    return <div className="gameCell" onClick={props.winnieMove}> {cellContent}</div>
  })


  return (
    <div className="gameBoard">
      {renderMap}
    </div>
  )
}

export default GameMap;
